import React from 'react'
import { useState,useEffect } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'

export default function Home() {
    const[data,setData]=useState()
    const API_KEY='ebd97901b6ca451482a89a2881456f4f'

    async function  GetData(){
        let data = await axios.get('https://api.rawg.io/api/games?key=ebd97901b6ca451482a89a2881456f4f')
        setData(data.data.results)
        console.log(data.data.results)
    }
    async function GetInfo(){
        let id =3328
        let info = await axios.get(`https://api.rawg.io/api/games/${id}?key=ebd97901b6ca451482a89a2881456f4f`)
        console.log(info.data)
    }


    useEffect(()=>{
        GetData()
        GetInfo()
    },[])


return (
    <>
    <h1>Welcome to Home Page</h1>
    {data?<div className='row g-2'>
        {data.map((game,index)=>(
            <div className='col-md-4' key={index}>
                <div className='card overflow-hidden rounded shadow'>
                    <Link to={"/GameDetails/"+game.id}>
                    <img src={game.short_screenshots[0].image} className="" style={{width:'50rem',height:'250px'}}></img>
                    </Link>
                    <div className='card-body'>
                        <h5 className='card-title'>{game.name}</h5>
                    </div>
                    <div className='ratings rounded'>
                        <p >{game.rating}</p>
                    </div>
                </div>
            </div>
            
        ))}
    </div>:''}
    
    </>
)
}
