import React from 'react'
import { useState,useEffect } from 'react'
import axios from 'axios'

export default function Home() {
    const[data,setData]=useState()
    const API_KEY='ebd97901b6ca451482a89a2881456f4f'
    async function  GetData(){
        let data = await axios.get('https://rawg-video-games-database.p.rapidapi.com/games',{
            headers: {
                'X-RapidAPI-Key': '3537e5089dmsh688824756f9869fp163939jsn86516cb11696',
                'X-RapidAPI-Host': 'opencritic-api.p.rapidapi.com'
            }
        })
        setData(data.data)
        console.log(data.data)
    }

    useEffect(()=>{
        GetData()
    },[])


return (
    <>
    <h1>Welcome to Home Page</h1>
    {data?<div className='row g-2'>
        {data.map((game,index)=>(
            <div className='col-md-4' key={index}>
                <div className='card'>
                    <img src={"https://opencritic-api.p.rapidapi.com/"+game.images.banner.sm}></img>
                    <div className='card-body'>
                        <h5 className='card-title'>{game.name}</h5>
                    </div>
                </div>
            </div>
            
        ))}
    </div>:''}
    
    </>
)
}
