import React from 'react'
import { useState,useEffect } from 'react'
import axios from 'axios'

export default function Home() {
    const[data,setData]=useState()
    const API_KEY='ebd97901b6ca451482a89a2881456f4f'
    async function  GetData(){
        let data = await axios.get('https://api.rawg.io/api/games?key=ebd97901b6ca451482a89a2881456f4f')
        setData(data.data.results)
        console.log(data.data.results)
    }

    useEffect(()=>{
        GetData()
    },[])


return (
    <>
    <h1>Welcome to Home Page</h1>
    {data?<div className='row g-2'>
        {data.map((game,index)=>(
            <div className='col-md-4' key={index}>
                <div className='card'>
                    <img src={game.short_screenshots[0].image} className="" style={{width:'150px',height:'150px'}}></img>
                    <div className='card-body'>
                        <h5 className='card-title'>{game.name}</h5>
                    </div>
                </div>
            </div>
            
        ))}
    </div>:''}
    
    </>
)
}
