import React from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
import { useEffect,useState } from 'react'

export default function GameDetails() {
  let {id} = useParams()
  const [game,setGame]=useState()

  async function GetGameDetails(id){
    let data = await axios.get(`https://api.rawg.io/api/games/${id}?key=ebd97901b6ca451482a89a2881456f4f`)
    setGame(data.data)
    console.log(data.data)
  }
  useEffect(()=>{
    GetGameDetails(id)
  },[])

  return (
    <>
    {game?
    <>
      <div className='row g-3'>
        <div className='col-md-4'>
          <div className='card rounded overflow-hidden shadow'>
          <img src={game.background_image} className="w-100 h-100"></img>
          <div  className='p-2'>
            <h5>{game.name}</h5>
          </div>
          </div>         
        </div>
        <div className='col-md-6'>
          <h3>released : {game.released}</h3>
          <h3>rating : {game.rating}</h3>
          <p><span>Description : </span>{game.description_raw}</p>
        </div>

      </div>
      </>
      :<div>GameDetails {id}</div>}
    </>
  )
}
