import React from 'react'
import { useState,useEffect } from 'react'
import axios from 'axios'
import { Link, Outlet } from 'react-router-dom'

export default function Home() {
    const[data,setData]=useState([])
    const[tag,setTag]=useState()
    const [loaded,setLoading]=useState(false)
    const API_KEY='ebd97901b6ca451482a89a2881456f4f'

    async function  GetData(){
        let data = await axios.get('https://api.rawg.io/api/games?key=ebd97901b6ca451482a89a2881456f4f')
        setData(data.data.results)
        console.log(data.data.results) 
    }

    function getFilterdData(value){
        setData()
        setTag(value)
        console.log(tag)
        let keepers = []
        data.forEach(element => {
            element.genres.forEach(genre=>{
                if(genre.name.includes(tag)){
                    keepers.push(element)
                }
            })
        })
        console.log(keepers)
        setData(keepers)
    }
    useEffect(()=>{
    setLoading(true);
    GetData()
    setTimeout(() => {
    setLoading(false);
    }, 2000)
    },[])


return (
    <>
    <h1 className='mb-3'>Welcome</h1>
    <div className='mb-3'>
    <span className='mb-3 fs-3'>Related Tags : </span>
    <span className='bg-dark rounded-pill text-white  me-5 p-2 home-tags'  onClick={(e)=>{getFilterdData(e.target.textContent)}}>Shooter</span>
    <span className='bg-dark rounded-pill text-white  me-5 p-2 home-tags'  onClick={(e)=>{getFilterdData(e.target.textContent)}}>Action</span>
    <span className='bg-dark rounded-pill text-white  me-5 p-2 home-tags'  onClick={(e)=>{getFilterdData(e.target.textContent)}}>RPG</span>
    <span className='bg-dark rounded-pill text-white  me-5 p-2 home-tags' onClick={(e)=>{getFilterdData(e.target.textContent)}}>Puzzle</span>
    <span className='remove-tags' onClick={()=>{GetData()}}>Remove Tags</span>
    </div>
    <Outlet></Outlet>
    </>
)
}
