import React, { useState } from 'react';

export default function Account({user}){
  const [editing, setEditing] = useState(false);
  const [userInfo, setUserInfo] = useState('ahmed');
  const [profilePictureFile, setProfilePictureFile] = useState(null);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUserInfo({ ...userInfo, [name]: value });
  };

  const handleEditClick = () => {
    setEditing(true);
  };

  const handleSaveClick = () => {
    // save the user's updated information to the server
    // setEditing to false
    setEditing(false);
  };

  const handleCancelClick = () => {
    // reset the user's information to its original state
    // setEditing to false
    setEditing(false);
  };

  const handleFileInputChange = (event) => {
    setProfilePictureFile(event.target.files[0]);
  };

  const handleUploadClick = () => {
    // upload the user's new profile picture to the server
    // update the userInfo state with the new profile picture URL
    // set the profilePictureFile state to null
    setProfilePictureFile(null);
  };

  return (
    <div className="account-page">
      <div className="account-header">
        <h1>Account Information</h1>
        {!editing && <button onClick={handleEditClick}>Edit</button>}
      </div>
      <div className="account-content">
        <div className="profile-picture">
          {!editing ? (
            <img src={userInfo.profilePicture} alt="Profile" />
          ) : (
            <>
              <input type="file" onChange={handleFileInputChange} />
              {profilePictureFile && (
                <button onClick={handleUploadClick}>Upload</button>
              )}
            </>
          )}
        </div>
        <div className="user-info">
          <label>Email:</label>
          {!editing ? (
            <p>{userInfo.email}</p>
          ) : (
            <input
              type="email"
              name="email"
              value={userInfo.email}
              onChange={handleInputChange}
            />
          )}
          <label>Password:</label>
          {!editing ? (
            <p>{userInfo.password}</p>
          ) : (
            <input
              type="password"
              name="password"
              value={userInfo.password}
              onChange={handleInputChange}
            />
          )}
          <label>Username:</label>
          {!editing ? (
            <p>{userInfo.username}</p>
          ) : (
            <input
              type="text"
              name="username"
              value={userInfo.username}
              onChange={handleInputChange}
            />
          )}
        </div>
      </div>
      {editing && (
        <div className="account-footer">
          <button onClick={handleSaveClick}>Save</button>
          <button onClick={handleCancelClick}>Cancel</button>
        </div>
      )}
    </div>
  );
};
