import React, { useEffect, useState } from 'react';
import { useContext } from 'react';
import { UserLogged } from '../Store/UserContextStore';
import axios from 'axios';
import LoadingScreen from '../Loading/Loading';

export default function Account(){
  let user = useContext(UserLogged)
  const [editing, setEditing] = useState(false);
  const [userName, setUserName] = useState('');
  const [loaded,setLoading]=useState(false)
  const [userEmail,setEmail]=useState('')
  const [userPassword, setUserPassword] = useState('');
  const [profilePictureFile, setProfilePictureFile] = useState(null);

  const handleInputChange = (event) => {
    
  };

  const handleEditClick = () => {
    setEditing(true);
  };

  const handleSaveClick = () => {
    // save the user's updated information to the server
    // setEditing to false
    setEditing(false);
  };

  const handleCancelClick = () => {
    // reset the user's information to its original state
    // setEditing to false
    setEditing(false);
  };

  const handleFileInputChange = (event) => {
    setProfilePictureFile(event.target.files[0]);
  };
  async function GetInfo(){
    console.log(user)
    await axios.get(`http://localhost:3001/AccountInfo?user=${user.name}`).then(res=>{
        console.log(res.data)
        setUserName(res.data.username)
        setEmail(res.data.email)
        setUserPassword(res.data.password)
    }).catch(err=>{
        console.log(err)
    })
  }

  const handleUploadClick = () => {
    // upload the user's new profile picture to the server
    // update the userInfo state with the new profile picture URL
    // set the profilePictureFile state to null
    setProfilePictureFile(null);
  };

  useEffect(()=>{
    setLoading(true);
    GetInfo()
    setTimeout(() => {
    setLoading(false);
    },500)
  },[])

  return (
    <>
    {loaded?<LoadingScreen/>:<div className="account-page text-white">
      <div className="account-header">
        <h1>Account Information</h1>
        {!editing && <button onClick={handleEditClick}>Edit</button>}
      </div>
      <div className="account-content">
        <div className="profile-picture">
          {!editing ? (
            <img src={''} alt="Profile" />
          ) : (
            <>
              <input type="file" onChange={handleFileInputChange} />
              {profilePictureFile && (
                <button onClick={handleUploadClick}>Upload</button>
              )}
            </>
          )}
        </div>
        <div className="user-info">
        <label>Email:</label>
          {!editing ? (
            <p>{userEmail}</p>
          ) : (
            <input
              type="email"
              name="email"
              value={userEmail}
            />
          )}
          
          <label>Username:</label>
          {!editing ? (
            <p>{userName}</p>
          ) : (
            <input
              type="text"
              name="username"
              value={setUserName}
              onChange={handleInputChange}
            />
          )}
          <label>Password:</label>
          {!editing ? (
            <p>{userPassword}</p>
          ) : (
            <input
              type="password"
              name="password"
              value={setUserPassword}
              onChange={handleInputChange}
            />
          )}
        </div>
      </div>
      {editing && (
        <div className="account-footer">
          <button onClick={handleSaveClick}>Save</button>
          <button onClick={handleCancelClick}>Cancel</button>
        </div>
      )}
    </div>}
    
    </>
  );
};
