import React from 'react'
import { useEffect,useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'

export default function MainHome() {
    const[data,setData]=useState([])
    const [loaded,setLoading]=useState(false)
    const API_KEY='ebd97901b6ca451482a89a2881456f4f'

    async function  GetData(){
        let data = await axios.get('https://api.rawg.io/api/games?key=ebd97901b6ca451482a89a2881456f4f')
        setData(data.data.results)
        console.log(data.data.results) 
    }
    useEffect(()=>{
    setLoading(true);
    GetData()
    setTimeout(() => {
    setLoading(false);
    },500)
    },[])

  return (
    <>
    <h3>currently viewing all games</h3>
    {loaded?<div className='h-100 d-flex justify-content-center align-items-center'>
        <div className='loading'>
            <h2 className='text-dark'>Loading assets...</h2>
        </div>
        </div>:
        <div className='row g-2'>
        {data.map((game,index)=>(
            <div className='col-md-4' key={index}>
                <div className='card overflow-hidden rounded shadow'>
                    <Link to={"/GameDetails/"+game.id}>
                    <img src={game.short_screenshots[0].image} className="" style={{width:'27rem',height:'15.6rem'}}></img>
                    </Link>
                    <div className='card-body'>
                        <h5 className='card-title text-dark'>{game.name}</h5>
                    </div>
                    <button></button>
                    <div className='ratings rounded'>
                        <p >{game.rating}</p>
                    </div>
                </div>
            </div>
            
        ))}
    </div>}
    </>
  )
}
