import React from 'react'
import { useEffect,useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import { NavLink } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'

export default function MainHome() {
    let navigate = useNavigate()
    const[data,setData]=useState([])
    const [loaded,setLoading]=useState(false)
    const API_KEY='ebd97901b6ca451482a89a2881456f4f'

    async function  GetData(){
        let data = await axios.get(`https://api.rawg.io/api/games?key=ebd97901b6ca451482a89a2881456f4f`)
        setData(data.data.results)
        console.log(data.data.results) 
    }
    
    function NavigateFilter(e,value){
        e.preventDefault()
        navigate(`/home/Tags/${value}`,{Filter:value})
        }
    useEffect(()=>{
    setLoading(true);
    GetData()
    setTimeout(() => {
    setLoading(false);
    },500)
    },[])

  return (
    <>
    
     <h1 className='mb-3'>Welcome {user.name?user.name:''}</h1>
    <div className='mb-3'>
    <h3 className='mb-3 d-inline'>Related Tags : </h3>
    <NavLink className='bg-dark rounded-pill text-white  me-2 p-2 home-tags' onClick={(e)=>{NavigateFilter(e,e.target.textContent)}}>Shooter</NavLink>
    <NavLink className='bg-dark rounded-pill text-white  me-2 p-2 home-tags' onClick={(e)=>{NavigateFilter(e,e.target.textContent)}}>Action</NavLink>
    <NavLink className='bg-dark rounded-pill text-white  me-2 p-2 home-tags' onClick={(e)=>{NavigateFilter(e,e.target.textContent)}}>RPG</NavLink>
    <NavLink className='bg-dark rounded-pill text-white  me-2 p-2 home-tags' onClick={(e)=>{NavigateFilter(e,e.target.textContent)}}>Puzzle</NavLink>
    <span className='remove-tags text-white' onClick={()=>{navigate('/home')}}>Remove Tags</span>
    </div>
    <h3>currently viewing all games</h3>
    {loaded?<div className='h-100 d-flex justify-content-center align-items-center'>
        <div className='loading'>
            <h2 className=''>Loading assets...</h2>
        </div>
        </div>:
        <div className='row g-2'>
        {data.map((game,index)=>(
            <div className='col-md-4' key={index}>
                <div className='card overflow-hidden rounded shadow'>
                    <Link to={"/GameDetails/"+game.id}>
                    <img src={game.short_screenshots[0].image} className="" style={{width:'27rem',height:'15.6rem'}}></img>
                    </Link>
                    <div className='card-body'>
                        <h5 className='card-title text-dark'>{game.name}</h5>
                    </div>
                    <div className='text-center mb-2'>
                    <Link to={"/GameDetails/"+game.id}><button className='btn btn-outline-info w-50'>Click here for more info</button></Link>
                    </div>

                    <div className='ratings rounded'>
                        <p >{game.rating}</p>
                    </div>
                </div>
            </div>
            
        ))}
    </div>}
    </>
  )
}
