import React from 'react'
import {  useParams,Link } from 'react-router-dom'
import { useState,useEffect } from 'react'
import axios from 'axios'

export default function Filter() {
    let {filter} = useParams()
    const[data,setData]=useState([])
    const [loaded,setLoading]=useState(false)
    const[tag,setTag]=useState(filter)

 
    async function getFilterdData(value){
        let data = await axios.get('https://api.rawg.io/api/games?key=ebd97901b6ca451482a89a2881456f4f')
        data = data.data.results
        let keepers = []
        data.forEach(element => {
            element.genres.forEach(genre=>{
                if(genre.name.includes(tag)){
                    keepers.push(element)
                }
            })
        })
        setData(keepers)
    }

useEffect(()=>{
    setLoading(true);
    getFilterdData(filter)
    setTimeout(() => {
    setLoading(false);
    }, 500)
},[])   

return (
    <>
    <h3>Currently viewing <span className='active'>{filter}</span> games</h3>
    {loaded?<div className='h-100 d-flex justify-content-center align-items-center'>
        <div className='loading'>
            <h2 className=''>Loading assets...</h2>
        </div>
        </div>:
        <div className='row g-2'>
        {data.map((game,index)=>(
            <div className='col-md-4' key={index}>
                <div className='card overflow-hidden rounded shadow'>
                    <Link to={"/GameDetails/"+game.id}>
                    <img src={game.short_screenshots[0].image} className="" style={{width:'27rem',height:'15.6rem'}}></img>
                    </Link>
                    <div className='card-body'>
                        <h5 className='card-title text-dark'>{game.name}</h5>
                    </div>
                    <div className='ratings rounded'>
                        <p >{game.rating}</p>
                    </div>
                </div>
            </div>
            
        ))}
    </div>}
    </>
  )
}
