import React from 'react'
import { Link, Outlet } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'

export default function Home() {
    let navigate = useNavigate()

    function NavigateFilter(value){
    navigate(`/home/Utags/${value}`,{Filter:value})
    }


return (
    <>
    <h1 className='mb-3'>Welcome</h1>
    <div className='mb-3'>
    <span className='mb-3 fs-3'>Related Tags : </span>
    <span className='bg-dark rounded-pill text-white  me-2 p-2 home-tags' onClick={(e)=>{NavigateFilter(e.target.textContent)}}>Shooter</span>
    <span className='bg-dark rounded-pill text-white  me-2 p-2 home-tags' onClick={(e)=>{NavigateFilter(e.target.textContent)}} >Action</span>
    <span className='bg-dark rounded-pill text-white  me-2 p-2 home-tags' onClick={(e)=>{NavigateFilter(e.target.textContent)}} >RPG</span>
    <span className='bg-dark rounded-pill text-white  me-2 p-2 home-tags' onClick={(e)=>{NavigateFilter(e.target.textContent)}}>Puzzle</span>
    <span className='remove-tags' onClick={()=>{navigate('/home')}}>Remove Tags</span>
    </div>
    <Outlet></Outlet>
    </>
)
}
