import { Link, Outlet } from 'react-router-dom'
import { Navigate } from 'react-router-dom'

export default function Home() {
    let navigate = Navigate()
    function NavigateFilter(value){
        console.log(value)
        navigate(`/topic/${props.id}`,{state:{id:props.id}});
    }


return (
    <>
    <h1 className='mb-3'>Welcome</h1>
    <div className='mb-3'>
    <span className='mb-3 fs-3'>Related Tags : </span>
    <span className='bg-dark rounded-pill text-white  me-5 p-2 home-tags' >Shooter</span>
    <span className='bg-dark rounded-pill text-white  me-5 p-2 home-tags'  >Action</span>
    <span className='bg-dark rounded-pill text-white  me-5 p-2 home-tags'  >RPG</span>
    <span className='bg-dark rounded-pill text-white  me-5 p-2 home-tags' onClick={(e)=>{NavigateFilter(e.target.textContent)}}>Puzzle</span>
    <span className='remove-tags'>Remove Tags</span>
    </div>
    <Outlet></Outlet>
    </>
)
}
