import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react';

export default function MainLoading({children}) {

    const [loading, setLoading] = useState(true);

    useEffect(() => {
      setLoading(true);
      setTimeout(() => {
        setLoading(false);
      }, 2000); // Use setTimeout or any other way to simulate loading
    },[]);

  return (

    <>
    <div className={`loading-screen ${loading ? 'loading' : 'hide'}`} onAnimationEnd={onAnimationEnd}>
      <div className="spinner"></div>
    </div>
    </>
  )
}
