import React from 'react'
import { useState,useEffect } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'

export default function Home() {
    const[data,setData]=useState([])
    const[tag,setTag]=useState()
    const API_KEY='ebd97901b6ca451482a89a2881456f4f'

    async function  GetData(){
        let data = await axios.get('https://api.rawg.io/api/games?key=ebd97901b6ca451482a89a2881456f4f')
        setData(data.data.results)
        console.log(data.data.results)      
    }

    function getFilterdData(){
        let Filtered = data.map((game)=>{
        game.genres.filter(item=>{
            if(item.name==tag){
                console.log(item.name)
                setData(game)
            }
        })
        })       
        console.log(Filtered)
    }
    useEffect(()=>{
        GetData()
    },[])

    useEffect(()=>{
        getFilterdData()
    },[tag])

return (
    <>
    <h1 className='mb-3'>Welcome to Home Page</h1>
    <div className='mb-3'>
    <span className='mb-3 fs-3'>Related Tags : </span>
    <span className='bg-dark rounded-pill text-white p-2 me-2 '>Shooter</span>
    <span className='bg-dark rounded-pill text-white  p-2 me-2'>Action</span>
    <span className='bg-dark rounded-pill text-white  p-2 me-2'>RPG</span>
    <span className='bg-dark rounded-pill text-white  p-2 me-2' onClick={(e)=>{setTag(e.target.textContent)}}>Puzzle</span>
    </div>

    {data?<div className='row g-2'>
        {tag?data.map(game=>{
            <div className='col-md-4' key={index}>
            <div className='card overflow-hidden rounded shadow'>
                <Link to={"/GameDetails/"+game.id}>
                <img src={game.short_screenshots[0].image} className="" style={{width:'27rem',height:'15.6rem'}}></img>
                </Link>
                <div className='card-body'>
                    <h5 className='card-title'>{game.name}</h5>
                </div>
                <div className='ratings rounded'>
                    <p >{game.rating}</p>
                </div>
            </div>
        </div>
        }):data.map((game,index)=>(
            <div className='col-md-4' key={index}>
                <div className='card overflow-hidden rounded shadow'>
                    <Link to={"/GameDetails/"+game.id}>
                    <img src={game.short_screenshots[0].image} className="" style={{width:'27rem',height:'15.6rem'}}></img>
                    </Link>
                    <div className='card-body'>
                        <h5 className='card-title'>{game.name}</h5>
                    </div>
                    <div className='ratings rounded'>
                        <p >{game.rating}</p>
                    </div>
                </div>
            </div>
            
        ))}
        {}
    </div>:''}
    
    </>
)
}
