import React, { useContext } from 'react'
import {  Outlet } from 'react-router-dom'
import { UserLogged } from '../Store/UserContextStore'
import Helmet from 'react-helmet'
import { Power3 } from "gsap/gsap-core";
import { gsap} from "gsap";
import { useRef,useLayoutEffect } from 'react'


export default function Home() {
    let user = useContext(UserLogged)
    const errorMessage = useRef()

    function close(){
        const CloseMessage = document.getElementById('alert-message')
        gsap.to(errorMessage.current,.4,{ease:Power3.easeOut,x:100,opacity:0}).then(()=>CloseMessage.remove())
      }
      useLayoutEffect(()=>{
        gsap.from(errorMessage.current,.8,{ease:Power3.easeOut,x:100,opacity:0})
      },[])
    


return (
    <>
    <Helmet>
        <title>Home</title>
    </Helmet>
    {user.error?<div className="alert alert-danger" id="alert-message" ref={errorMessage}>
        {user.error}
        <i className="fa-solid fa-xmark close-icon" onClick={()=>{close()}}></i>
        </div>:''}
   
    <Outlet></Outlet>
    </>
)
}
